package com.evergent.corejava.Strings;

/**
 * @author Akhil.Akula
 *
 */
public class StringDemo3_Methods {
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String name ="   HelloWorld";
		System.out.println(name.length()); //Returns the length of this string. 
		System.out.println(name.toLowerCase());//Converts all of the characters in this String to lowercase 
		System.out.println(name.toUpperCase());//Converts all of the characters in this String to uppercase 
		System.out.println(name.trim());//Returns a string whose value is this string, with all leadingand trailing space removed

	}

}
